import React, { Component } from "react";
import {
  Machine,
  State,
  actions,
  assign,
  send,
  sendParent,
  interpret,
  spawn,
} from "xstate";

const promiseMachine = Machine({
  id: "promise",
  initial: "pending",
  state: {
    pending: {
      on: {
        RESOLVE: "resolved",
        REJECT: "rejected",
      },
    },
    resolved: {
      type: "final",
    },
    rejected: {
      type: "final",
    },
  },
});

const promiseService = interpret(promiseMachine).onTransition((state) =>
  console.log(state.value)
);

class Home extends Component {
  teste = () => {
    console.log("teste");
  };

  start = () => {
    console.log("start");
    promiseService.start();
  };

  send = () => {
    console.log("send");
    promiseService.send("RESOLVE");
  };

  render() {
    return (
      <div>
        <button type="button" onClick={this.teste}>
          teste
        </button>
        <button type="button" onClick={() => this.start}>
          Start
        </button>
        <button type="button" onClick={() => this.send}>
          Send
        </button>
      </div>
    );
  }
}

export default Home;
