// export default Home;

import { Machine, interpret } from "xstate";

const machine = Machine({
  id: "trafficlights",
  initial: "green",
  states: {
    green: {
      on: {
        TIMER: "yellow",
      },
    },
    yellow: {
      on: {
        TIMER: "red",
      },
    },
    red: {
      on: {
        TIMER: "green",
      },
    },
  },
});

const currentState = machine.initialState.value;
const newState = machine.transition(currentState, "TIMER");
