import React, { useState, useMemo, useEffect } from "react";
import { Machine, interpret } from "xstate";

const useMachine = (machine) => {
  const [current, setCurrent] = useState(machine.initialState);

  const service = useMemo(
    () =>
      interpret(machine)
        .onTransition((state) => {
          if (state.changed) {
            setCurrent(state);
          }
        })
        .start(),
    []
  );

  useEffect(() => {
    return () => service.stop();
  }, []);

  return [current, service.send];
};

const todoMachine = Machine({
  initial: "all",
  context: {
    list: [
      { id: 1, value: "task 1", complete: false },
      { id: 2, value: "task 2", complete: false },
      { id: 3, value: "task 3", complete: false },
      { id: 4, value: "task 4", complete: false },
    ],
  },
  states: {
    all: {
      // all
    },
    active: {
      // actives
    },
    complete: {
      // completes
    },
  },
});

const Todos = () => {
  const [machine, send] = useMachine(todoMachine);
  const [todos, getTodos] = machine.context.tolistdos;

  return (
    <div>
      <form>
        <input
          type="text"
          id="todo"
          name="todo"
          placeholder="Task here"
        ></input>
      </form>

      {todos &&
        todos.map((t) => (
          <li key={t.id} style={t.styled}>
            <input
              type="checkbox"
              id={t.id}
              name="register"
              value={t.value}
              onChange={(e) => this.props.change(e)}
              checked={t.complete}
            />
            {t.value}
          </li>
        ))}

      <button type="button" onClick={{}}>
        All
      </button>
      <button type="button" onClick={{}}>
        Active
      </button>
      <button type="button" onClick={{}}>
        Complete
      </button>
    </div>
  );
};

export default Todos;
